INSERT INTO `sys_oper_log` VALUES ('612', '歌手信息', '2', 'com.ruoyi.web.controller.business.SingerController.edit()', 'PUT', '1', 'admin', null, '/business/singer', '127.0.0.1', '内网IP', '{\"year\":20,\"influencePower\":\"2009年12月14日，参加中央音乐广播大型慈善活动《我要上学-爱的5003慈善夜》。\\n2011年4月29日，后弦和sara举办歌友会并拍卖二手物品为灾区同胞募集。 [48] \\n2013年5月23日，出席石家庄“2013财智中国峰会·公益盛典”。 [49] \\n2016年10月发行专辑《下完这场雨》 [5]  。\\n2017年2月，受邀参加中国音乐联播榜年度盛典并获“电台推荐年度金曲”奖 [6]  。\",\"delFlag\":0,\"sinName\":\"后弦\",\"curRank\":5,\"sinId\":5,\"nickname\":\"老大\",\"followerNumber\":0,\"performingExp\":\"2005年9月，签约东升国际传媒，发行首张全创作专辑《古玩》，两个月专辑销量即突破十万张。12月16日，后弦举办\\\"龙腾北京\\\"歌友会，中国音像评价网与龙腾音乐榜联合颁发给后弦“销量双白金”大奖。并凭此专辑获得《亚太音乐榜》“最具人气新人奖”。 [1]\",\"albumNumber\":0,\"area\":\"后弦（Hoho），1979年12月14日出生于广西壮族自治区柳州市，中国内地男歌手，词曲创作人。\",\"lastRank\":9,\"sex\":1,\"updateTime\":1605876187207,\"params\":{},\"picture\":\"/group1/M00/00/01/rBCyFF-3uYeAb-lwAAClYuRwAMM938.png\",\"mvNumber\":0,\"songNumber\":0,\"humanQi\":6,\"detail\":\"2005年，发行个人首张全创作专辑《古玩》而正式出道，两个月专辑销量突破十万张，并获得《亚太音乐榜》最具人气新人奖\",\"sinType\":0,\"earlyExp\":\"1997年，就读广西大学，求学时开始接触原创音乐，大学时代发起组织的B2B和声组合获广西最受欢迎学生创作组合，此外，还组织了HBH四人青春组合，短路乐队。连续三年获广西大学校园十大歌手称号。\"}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-20 20:43:07');
