INSERT INTO `sys_oper_log` VALUES ('626', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.edit()', 'PUT', '1', 'admin', null, '/business/song', '127.0.0.1', '内网IP', '{\"songName\":\"海阔天空\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w8aALuo4AADPZOmQLRU681.png\",\"issuingDate\":1605283200000,\"hqUrl\":\"\",\"delFlag\":0,\"singerList\":[{\"year\":30,\"influencePower\":\"1983年，通过Tom Lee琴行老板的介绍，黄家驹与叶世荣结识并结为好友，两人发觉彼此音乐取向都一致，于是合组乐队；而负责弹奏主音吉他的邓炜谦把乐队命名为Beyond [16]  ；乐队组建后，参加了《吉他》杂志举办的“山叶吉他比赛”，并获得冠军，此时乐队由四名成员组成，分别是主唱兼节奏吉他手黄家驹、主音吉他手邓炜谦、贝斯手李荣潮以及鼓手叶世荣 [17]  ；\",\"delFlag\":0,\"sinName\":\"Beyoud\",\"sinId\":14,\"nickname\":\"Beyoud\",\"followerNumber\":0,\"performingExp\":\"1988年，签约新艺宝唱片公司 [29]  ；3月，发行粤语专辑《现代舞台》，该专辑不仅是Beyond批判社会现象的开端，而且音乐走向相比Beyond以往专辑更显柔和，黄贯中和黄家强首次拥有自己的配唱歌曲，开始兼任乐队的主唱；\",\"albumNumber\":0,\"area\":\"无\",\"sex\":1,\"updateTime\":1605171548000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"sinType\":0,\"earlyExp\":\"1986年，刘志远以吉他手和键盘手的身份加入Beyond；之后，他们和小岛乐队、达明一派合录了一盘盒带《劲歌金曲》，并收录了他们创作的4首歌曲；4月，Beyond自己出资举办香港中环演艺会；同年，叶世荣和黄贯中、邓炜谦、马永基组成一支名为“高速啤机”的重金属乐队；\"}],\"duration\":200,\"songId\":3,\"stIds\":[4,13],\"lyrId\":2,\"updateTime\":1605878728261,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-VmAMVqHAEm9EZX1YNg310.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/00/rBCyFF-s9E2AX8HxAAHSvlCRah8056.png\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":14,\"slId\":0,\"updateTime\":1605171548000,\"params\":{},\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"playNumber\":30},\"sinIds\":[14],\"songTypeList\":[{\"stName\":\"古典\",\"stId\":4,\"updateTime\":1605171548000,\"delFlag\":0,\"params\":{}},{\"stName\":\"舞曲\",\"stId\":13,\"updateTime\":1605171548000,\"delFlag\":0,\"params\":{}}],\"isCharge\":1,\"todayPlay\":500,\"isCopyright\":1,\"playNumber\":30}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-20 21:25:28');
INSERT INTO `sys_oper_log` VALUES ('627', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.edit()', 'PUT', '1', 'admin', null, '/business/song', '127.0.0.1', '内网IP', '{\"songName\":\"有没有那么一首歌\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w_KATXqJAAF88bqpisA748.png\",\"issuingDate\":1592236800000,\"delFlag\":0,\"singerList\":[{\"year\":20,\"influencePower\":\"1984年，发行个人第一首创作单曲《谁曾说过》，这首歌曲收录在他与林良乐、钱榫鑫以及台风合唱团共同推出的音乐合辑《名歌三人行》中；同年，在张淘淘发行的专辑《抱着你的感觉》中参与演唱歌曲《梦21》。\",\"delFlag\":0,\"sinName\":\"周华健\",\"sinId\":1,\"nickname\":\"华建哥\",\"followerNumber\":0,\"performingExp\":\"2020年7月15日，周华健凭借专辑《少年》获得第31届台湾金曲奖最佳国语男歌手提名；10月3日，专辑《少年》获得第31届台湾金曲奖最佳演唱录音专辑奖\",\"albumNumber\":0,\"area\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区\",\"sex\":1,\"updateTime\":1605171578000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区，华语流行乐男歌手、音乐人、演员 [1]  ，毕业于台湾大学。\",\"sinType\":0,\"earlyExp\":\"周杰伦出生于台湾省新北市，祖籍福建省泉州市永春县 [23]  。周杰伦4岁的时候，母亲叶惠美把他送到淡江山叶幼儿音乐班学习钢琴 [24]  。周杰伦读初中二年级时，他的父母因性格不合离婚，而周杰伦则归母亲叶惠美抚养。中考时，周杰伦没有考上普通高中；同年，他因为擅长钢琴而被淡江中学第一届音乐班录取。高中毕业以后，他两次报考台北大学音乐系均没有被录取，于是他开始在一家餐馆打工 [24]  。\\n1997年9月，周杰伦在母亲的鼓励下报名参加了台北星光电视台的娱乐节目《超级新人王》，并在节目中邀人演唱了自己创作的歌曲《梦有翅膀》。当主持人吴宗宪看到这首歌曲的曲谱后，就邀请周杰伦到阿尔发音乐公司担任音乐助理 [24]  。1998年，他创作了歌曲《眼泪知道》，在公司把这首歌曲给刘德华以后却被退歌；而他为张惠妹创作的歌曲《双截棍》和《忍者》（后收录于周杰伦个人专辑《范特西》中）也被退回 [25-26]  。\"}],\"duration\":200,\"songId\":5,\"stIds\":[15],\"lyrId\":1,\"updateTime\":1605878771947,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-XGAF0tSAEm9EZX1YNg636.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/00/rBCyFF-s9HqANMQfAAEcqOOlq4M457.jpg\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":1,\"slId\":0,\"updateTime\":1605171578000,\"params\":{},\"songNumber\":0,\"detail\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区，华语流行乐男歌手、音乐人、演员 [1]  ，毕业于台湾大学。\",\"playNumber\":11},\"sinIds\":[1],\"songTypeList\":[{\"stName\":\"流行\",\"stId\":15,\"updateTime\":1605171578000,\"