INSERT INTO `sys_oper_log` VALUES ('633', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.edit()', 'PUT', '1', 'admin', null, '/business/song', '127.0.0.1', '内网IP', '{\"songName\":\"我不愿让你一个人\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3xPGAedUwAAF4bo63DdE243.png\",\"issuingDate\":1597766400000,\"delFlag\":0,\"singerList\":[{\"year\":36,\"influencePower\":\"品冠2012年结婚，2013年5月31日下午5时，其妻Jennifer剖腹产下一子，品冠为孩子取名“黄韦恩”，英文名为“Jayden” [32]  。今年5月31日，为迎接儿子小V一周岁生日，品冠还特别为儿子创作生日礼物《最美的问候(小V之歌)》。6月23日晚，品冠在新浪微博报喜，再获女儿。再次当爸爸的品冠激动地晒出女儿照片，大手牵着小手，十分有爱 [33]  。\",\"delFlag\":0,\"sinName\":\"品冠\",\"sinId\":11,\"nickname\":\"品冠\",\"followerNumber\":0,\"performingExp\":\"1994年，品冠与光良一起以“光良品冠”之名发行天主教首张本地歌集《我要跟随他》。参加马华公会主办全国华语创作歌曲比赛，凭借歌曲《演回自己》夺得季军 [7]  。\",\"albumNumber\":0,\"area\":\"品冠，本名黄品冠，1972年2月26日出生于马来西亚吉隆坡，祖籍中国广东揭阳。\",\"sex\":1,\"updateTime\":1605175877000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"995年签约中国台湾滚石唱片，并与光良组成歌唱组合无印良品，和光良在马来西亚发行首张创作专辑《掌心》。2000年无印良品解散，品冠开始个人发展；同年3月28日，发行首张个人专辑《疼你的责任》。2007年6月23日，品冠在北京举办首场个人售票演唱会。\",\"sinType\":0,\"earlyExp\":\"1988年到1990年，品冠获得全校歌唱比赛冠军。\\n1992年，品冠获得雪隆中学华语歌唱比赛冠军。\\n1993年，品冠凭借《永在我心里》获得第一届海螺新韵奖优胜作品。\"}],\"duration\":200,\"songId\":11,\"stIds\":[15],\"updateTime\":1605879027630,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-tCkSAA6xFAEYalQ5OAxg463.mp3\",\"isOriginal\":0,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/00/rBCyFF-s9amAcOkZAAByuwdDc60344.png\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":11,\"slId\":0,\"updateTime\":1605175877000,\"params\":{},\"songNumber\":0,\"detail\":\"995年签约中国台湾滚石唱片，并与光良组成歌唱组合无印良品，和光良在马来西亚发行首张创作专辑《掌心》。2000年无印良品解散，品冠开始个人发展；同年3月28日，发行首张个人专辑《疼你的责任》。2007年6月23日，品冠在北京举办首场个人售票演唱会。\",\"playNumber\":66},\"sinIds\":[11],\"songTypeList\":[{\"stName\":\"流行\",\"stId\":15,\"updateTime\":1605175877000,\"delFlag\":0,\"params\":{}}],\"isCharge\":0,\"todayPlay\":66,\"isCopyright\":1,\"playNumber\":66}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-20 21:30:27');
