INSERT INTO `sys_oper_log` VALUES ('649', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.edit()', 'PUT', '1', 'client', null, '/business/song', '127.0.0.1', '内网IP', '{\"songName\":\"有没有那么一首歌\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w_KATXqJAAF88bqpisA748.png\",\"lyrUrl\":\"/group1/M00/00/01/rBCyFF-7LWGAVR82AAAIpTFDqAY797.lrc\",\"issuingDate\":1592236800000,\"delFlag\":0,\"singerList\":[{\"year\":20,\"influencePower\":\"1984年，发行个人第一首创作单曲《谁曾说过》，这首歌曲收录在他与林良乐、钱榫鑫以及台风合唱团共同推出的音乐合辑《名歌三人行》中；同年，在张淘淘发行的专辑《抱着你的感觉》中参与演唱歌曲《梦21》。\",\"delFlag\":0,\"sinName\":\"周华健\",\"sinId\":1,\"nickname\":\"华建哥\",\"followerNumber\":0,\"performingExp\":\"2020年7月15日，周华健凭借专辑《少年》获得第31届台湾金曲奖最佳国语男歌手提名；10月3日，专辑《少年》获得第31届台湾金曲奖最佳演唱录音专辑奖\",\"albumNumber\":0,\"area\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区\",\"sex\":1,\"updateTime\":1605878772000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区，华语流行乐男歌手、音乐人、演员 [1]  ，毕业于台湾大学。\",\"sinType\":0,\"earlyExp\":\"周杰伦出生于台湾省新北市，祖籍福建省泉州市永春县 [23]  。周杰伦4岁的时候，母亲叶惠美把他送到淡江山叶幼儿音乐班学习钢琴 [24]  。周杰伦读初中二年级时，他的父母因性格不合离婚，而周杰伦则归母亲叶惠美抚养。中考时，周杰伦没有考上普通高中；同年，他因为擅长钢琴而被淡江中学第一届音乐班录取。高中毕业以后，他两次报考台北大学音乐系均没有被录取，于是他开始在一家餐馆打工 [24]  。\\n1997年9月，周杰伦在母亲的鼓励下报名参加了台北星光电视台的娱乐节目《超级新人王》，并在节目中邀人演唱了自己创作的歌曲《梦有翅膀》。当主持人吴宗宪看到这首歌曲的曲谱后，就邀请周杰伦到阿尔发音乐公司担任音乐助理 [24]  。1998年，他创作了歌曲《眼泪知道》，在公司把这首歌曲给刘德华以后却被退歌；而他为张惠妹创作的歌曲《双截棍》和《忍者》（后收录于周杰伦个人专辑《范特西》中）也被退回 [25-26]  。\"}],\"duration\":200,\"songId\":5,\"stIds\":[15],\"lyrId\":1,\"updateTime\":1606102369988,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-XGAF0tSAEm9EZX1YNg636.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w_KATXqJAAF88bqpisA748.png\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":1,\"slId\":0,\"updateTime\":1605878772000,\"params\":{},\"songNumber\":0,\"detail\":\"周华健（Emil Wakin Chau），1960年12月22日出生于中国香港西营盘，祖籍广东省汕头市潮南区，华语流行乐男歌手、音乐人、演员 [1]  ，毕业于台湾大学。\",\"playNumber\":11},\"sinIds\":[1],\"songTypeL