INSERT INTO `sys_oper_log` VALUES ('689', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.offSong()', 'PUT', '1', 'admin', null, '/business/song/offSong', '127.0.0.1', '内网IP', '{\"songName\":\"海口\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3wvSAKiKxAAGuRlvWr3E157.png\",\"lyrUrl\":\"/group1/M00/00/01/rBCyFF-7LUaASeTPAAAIZmSs-CQ443.lrc\",\"issuingDate\":1605283200000,\"hqUrl\":\"\",\"delFlag\":0,\"singerList\":[{\"year\":20,\"influencePower\":\"2009年12月14日，参加中央音乐广播大型慈善活动《我要上学-爱的5003慈善夜》。\\n2011年4月29日，后弦和sara举办歌友会并拍卖二手物品为灾区同胞募集。 [48] \\n2013年5月23日，出席石家庄“2013财智中国峰会·公益盛典”。 [49] \\n2016年10月发行专辑《下完这场雨》 [5]  。\\n2017年2月，受邀参加中国音乐联播榜年度盛典并获“电台推荐年度金曲”奖 [6]  。\",\"delFlag\":0,\"sinName\":\"后弦\",\"sinId\":5,\"nickname\":\"老大\",\"followerNumber\":0,\"performingExp\":\"2005年9月，签约东升国际传媒，发行首张全创作专辑《古玩》，两个月专辑销量即突破十万张。12月16日，后弦举办\\\"龙腾北京\\\"歌友会，中国音像评价网与龙腾音乐榜联合颁发给后弦“销量双白金”大奖。并凭此专辑获得《亚太音乐榜》“最具人气新人奖”。 [1]\",\"albumNumber\":0,\"area\":\"后弦（Hoho），1979年12月14日出生于广西壮族自治区柳州市，中国内地男歌手，词曲创作人。\",\"sex\":1,\"updateTime\":1606102343000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"2005年，发行个人首张全创作专辑《古玩》而正式出道，两个月专辑销量突破十万张，并获得《亚太音乐榜》最具人气新人奖\",\"sinType\":0,\"earlyExp\":\"1997年，就读广西大学，求学时开始接触原创音乐，大学时代发起组织的B2B和声组合获广西最受欢迎学生创作组合，此外，还组织了HBH四人青春组合，短路乐队。连续三年获广西大学校园十大歌手称号。\"}],\"timbreType\":3,\"duration\":200,\"songId\":2,\"lyrId\":3,\"updateTime\":1606548998443,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-TOAS4YBAEFy3MSRDKk186.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3wvSAKiKxAAGuRlvWr3E157.png\",\"delFlag\":0,\"slName\":\"容易陷入梦乡\",\"sinId\":5,\"slId\":0,\"updateTime\":1606102343000,\"params\":{},\"songNumber\":0,\"detail\":\"2005年，发行个人首张全创作专辑《古玩》而正式出道，两个月专辑销量突破十万张，并获得《亚太音乐榜》最具人气新人奖\",\"playNumber\":40},\"songTypeList\":[{\"updateTime\":1606102343000,\"delFlag\":0,\"params\":{}}],\"isCharge\":3,\"todayPlay\":40,\"isCopyright\":0,\"playNumber\":40}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-28 15:36:38');
INSERT INTO `sys_oper_log` VALUES ('690', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.offSong()', 'PUT', '1', 'admin', null, '/business/song/offSong', '127.0.0.1', '内网IP', '{\"songName\":\"海阔天空\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w8aALuo4AADPZOmQLRU681.png\",\"issuingDate\":1605283200000,\"hqUrl\":\"\",\"delFlag\":0,\"singerList\":[{\"year\":30,\"influencePower\":\"1983年，通过Tom Lee琴行老板的介绍，黄家驹与叶世荣结识并结为好友，两人发觉彼此音乐取向都一致，于是合组乐队；而负责弹奏主音吉他的邓炜谦把乐队命名为Beyond [16]  ；乐队组建后，参加了《吉他》杂志举办的“山叶吉他比赛”，并获得冠军，此时乐队由四名成员组成，分别是主唱兼节奏吉他手黄家驹、主音吉他手邓炜谦、贝斯手李荣潮以及鼓手叶世荣 [17]  ；\",\"delFlag\":0,\"sinName\":\"Beyoud\",\"sinId\":14,\"nickname\":\"Beyoud\",\"followerNumber\":0,\"performingExp\":\"1988年，签约新艺宝唱片公司 [29]  ；3月，发行粤语专辑《现代舞台》，该专辑不仅是Beyond批判社会现象的开端，而且音乐走向相比Beyond以往专辑更显柔和，黄贯中和黄家强首次拥有自己的配唱歌曲，开始兼任乐队的主唱；\",\"albumNumber\":0,\"area\":\"无\",\"sex\":1,\"updateTime\":1605878728000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"sinType\":0,\"earlyExp\":\"1986年，刘志远以吉他手和键盘手的身份加入Beyond；之后，他们和小岛乐队、达明一派合录了一盘盒带《劲歌金曲》，并收录了他们创作的4首歌曲；4月，Beyond自己出资举办香港中环演艺会；同年，叶世荣和黄贯中、邓炜谦、马永基组成一支名为“高速啤机”的重金属乐队；\"}],\"timbreType\":1,\"duration\":200,\"songId\":3,\"lyrId\":2,\"updateTime\":1606549379829,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-VmAMVqHAEm9EZX1YNg310.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w8aALuo4AADPZOmQLRU681.png\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":14,\"slId\":0,\"updateTime\":1605878728000,\"params\":{},\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"playNumber\":30},\"songTypeList\":[{\"updateTime\":1605878728000,\"delFlag\":0,\"params\":{}}],\"isCharge\":1,\"todayPlay\":500,\"isCopyright\":0,\"playNumber\":30}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-28 15:42:59');
