INSERT INTO `sys_oper_log` VALUES ('694', '歌曲', '2', 'com.ruoyi.web.controller.business.SongController.offSong()', 'PUT', '1', 'admin', null, '/business/song/offSong', '127.0.0.1', '内网IP', '{\"songName\":\"海阔天空\",\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w8aALuo4AADPZOmQLRU681.png\",\"issuingDate\":1605283200000,\"hqUrl\":\"\",\"delFlag\":0,\"singerList\":[{\"year\":30,\"influencePower\":\"1983年，通过Tom Lee琴行老板的介绍，黄家驹与叶世荣结识并结为好友，两人发觉彼此音乐取向都一致，于是合组乐队；而负责弹奏主音吉他的邓炜谦把乐队命名为Beyond [16]  ；乐队组建后，参加了《吉他》杂志举办的“山叶吉他比赛”，并获得冠军，此时乐队由四名成员组成，分别是主唱兼节奏吉他手黄家驹、主音吉他手邓炜谦、贝斯手李荣潮以及鼓手叶世荣 [17]  ；\",\"delFlag\":0,\"sinName\":\"Beyoud\",\"sinId\":14,\"nickname\":\"Beyoud\",\"followerNumber\":0,\"performingExp\":\"1988年，签约新艺宝唱片公司 [29]  ；3月，发行粤语专辑《现代舞台》，该专辑不仅是Beyond批判社会现象的开端，而且音乐走向相比Beyond以往专辑更显柔和，黄贯中和黄家强首次拥有自己的配唱歌曲，开始兼任乐队的主唱；\",\"albumNumber\":0,\"area\":\"无\",\"sex\":1,\"updateTime\":1606549380000,\"params\":{},\"mvNumber\":0,\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"sinType\":0,\"earlyExp\":\"1986年，刘志远以吉他手和键盘手的身份加入Beyond；之后，他们和小岛乐队、达明一派合录了一盘盒带《劲歌金曲》，并收录了他们创作的4首歌曲；4月，Beyond自己出资举办香港中环演艺会；同年，叶世荣和黄贯中、邓炜谦、马永基组成一支名为“高速啤机”的重金属乐队；\"}],\"timbreType\":1,\"duration\":200,\"songId\":3,\"lyrId\":2,\"updateTime\":1606549738389,\"params\":{},\"standardUrl\":\"/group1/M00/00/00/rBCyFF-s-VmAMVqHAEm9EZX1YNg310.mp3\",\"isOriginal\":1,\"isSingle\":1,\"songlistes\":{\"coverPicture\":\"/group1/M00/00/01/rBCyFF-3w8aALuo4AADPZOmQLRU681.png\",\"delFlag\":0,\"slName\":\"十一月的嚣张\",\"sinId\":14,\"slId\":0,\"updateTime\":1606549380000,\"params\":{},\"songNumber\":0,\"detail\":\"Beyond，中国香港摇滚乐队，成立于1983年，由黄家驹、黄贯中、黄家强、叶世荣组成。\",\"playNumber\":30},\"songTypeList\":[{\"updateTime\":1606549380000,\"delFlag\":0,\"params\":{}}],\"isCharge\":1,\"todayPlay\":500,\"isCopyright\":1,\"playNumber\":30}', '{\"msg\":\"操作成功\",\"code\":200}', '0', null, '2020-11-28 15:48:58');
